 *
 * @param database
 * @returns {Dictionary<mongoose/Model>}
 */
function alyacBuildModel(database) {
    const db = database || mongoose
    const models = _.chain(schemes)
        .mapKeys((v, k) => k.match(NAME_MATCH)[1])
        .mapValues((s, k) => db.model(k, s, k))
        .value()

    return models
}

